import axiosInstance from "../utils/axios";

const getLancheById = async (id) => {
    const res = await axiosInstance.get('/lanches/' + id);
    return res.data;
}

const getAllLanches = async () => {
    const res = await axiosInstance.get('/lanches');
    return res.data;
}

const createLanche = async (lancheData) => {
    const allLanches = await getAllLanches();
    const maxId = allLanches.reduce((max, lanche) => (lanche.id > max ? lanche.id : max), 0);
    const newId = maxId + 1;
    // Adiciona o ID ao objeto de dados do lanche
    const lancheWithId = { id: newId, ...lancheData };
    // Envia a solicitação para criar o lanche com o ID atualizado
    const res = await axiosInstance.post('/lanches', lancheWithId);
    return res.data;
}

const updateLancheById = async (id, updatedLancheData) => {
    const res = await axiosInstance.put(`/lanches/${id}`, updatedLancheData);
    return res.data;
}

const deleteLancheById = async (id) => {
    const res = await axiosInstance.delete(`/lanches/${id}`);
    return res.data;
}

export default {
    getLancheById,
    getAllLanches,
    createLanche,
    updateLancheById,
    deleteLancheById
}
