import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import LancheService from '../services/LancheService';

function CreateLancheForm() {
    const [produto, setProduto] = useState('');
    const [preco, setPreco] = useState('');
    const [imagem, setImagem] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await LancheService.createLanche({ produto, preco, imagem });
            console.log('Lanche criado com sucesso!');
        } catch (error) {
            // Lidar com erros de criação de lanche aqui
            console.error('Erro ao criar lanche:', error);
        }
    };

    return (
        <Form onSubmit={handleSubmit}>
            <Form.Group controlId="produto">
                <Form.Label>Produto</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Digite o nome do produto"
                    value={produto}
                    onChange={(e) => setProduto(e.target.value)}
                />
            </Form.Group>
            <Form.Group controlId="preco">
                <Form.Label>Preço</Form.Label>
                <Form.Control
                    type="number"
                    placeholder="Digite o preço"
                    value={preco}
                    onChange={(e) => setPreco(e.target.value)}
                />
            </Form.Group>
            <Form.Group controlId="imagem">
                <Form.Label>Imagem</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="URL da imagem"
                    value={imagem}
                    onChange={(e) => setImagem(e.target.value)}
                />
            </Form.Group>
            <Button variant="primary" type="submit">
                Criar Lanche
            </Button>
        </Form>
    );
}

export default CreateLancheForm;
